// –ö–ª–∞—Å—Å–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç (legacy –∫–æ–¥)
class UserProfile extends React.Component {
  constructor(props) {
    super(props);
