}

// –í–∞–ª–∏–¥–∞—Ü–∏—è –ø—Ä–æ–ø—Å–æ–≤ —Å PropTypes
import PropTypes from 'prop-types';

function ProductCard({ product, onAddToCart, isFeatured }) {
  // –ö–æ–º–ø–æ–Ω–µ–Ω—Ç–Ω–∞—è –ª–æ–≥–∏–∫–∞
  return (
    <div>
      <h3>{product.name}</h3>
      <button onClick={() => onAddToCart(product.id)}>
        Add to Cart
      </button>
    </div>
  );
}

ProductCard.propTypes = {
  // –û–±—ä–µ–∫—Ç product —Å –æ–ø—Ä–µ–¥–µ–ª—ë–Ω–Ω–æ–π —Å—Ç—Ä—É–∫—Ç—É—Ä–æ–π
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    inStock: PropTypes.bool
  }).isRequired,

  // –§—É–Ω–∫—Ü–∏—è, –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–∞—è
  onAddToCart: PropTypes.func.isRequired,

  // –ù–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–π –±—É–ª–µ–≤—ã–π –ø—Ä–æ–ø—Å —Å–æ –∑–Ω–∞—á–µ–Ω–∏–µ–º –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
  isFeatured: PropTypes.bool
};

ProductCard.defaultProps = {
  isFeatured: false
};

// TypeScript –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–∞
interface ProductCardProps {
  product: {
    id: number;
    name: string;
    price: number;
    inStock?: boolean;
  };
  onAddToCart: (productId: number) => void;
  isFeatured?: boolean;
}

function ProductCardTS({ product, onAddToCart, isFeatured = false }: ProductCardProps) {
  // –¢–∞ –∂–µ –ª–æ–≥–∏–∫–∞, –Ω–æ —Å TypeScript —Ç–∏–ø–∞–º–∏
}

// –ë–∞–∑–æ–≤–æ–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ useState
function Counter() {
  // –û–±—ä—è–≤–ª–µ–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const [count, setCount] = useState(0);

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —É–≤–µ–ª–∏—á–µ–Ω–∏—è —Å—á—ë—Ç—á–∏–∫–∞
  const increment = () => {
    setCount(count + 1); // –ü—Ä—è–º–æ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–±—Ä–æ—Å–∞
  const reset = () => {
    setCount(0); // –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ –∑–Ω–∞—á–µ–Ω–∏—è
  };

  return (
    <div className="counter">
      <h2>Count: {count}</h2>
      <div className="buttons">
        <button onClick={increment}>Increment</button>
        <button onClick={reset}>Reset</button>
      </div>
    </div>
  );
}

// –ü—Ä–æ–±–ª–µ–º–∞: –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è
function ProblematicCounter() {
  const [count, setCount] = useState(0);

  const incrementTwice = () => {
    // –ù–ï–ü–†–ê–í–ò–õ–¨–ù–û: –æ–±–∞ –≤—ã–∑–æ–≤–∞ –∏—Å–ø–æ–ª—å–∑—É—é—Ç –æ–¥–Ω–æ –∏ —Ç–æ –∂–µ –∑–Ω–∞—á–µ–Ω–∏–µ count
    setCount(count + 1); // count = 0 ‚Üí setCount(1)
    setCount(count + 1); // count –≤—Å—ë –µ—â—ë = 0 ‚Üí setCount(1)
    // –í —Ä–µ–∑—É–ª—å—Ç–∞—Ç–µ count —Å—Ç–∞–Ω–µ—Ç 1, –∞ –Ω–µ 2
  };

  // –†–µ—à–µ–Ω–∏–µ: –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏-–∫–æ–ª–±—ç–∫–∞
  const incrementTwiceCorrect = () => {
    setCount(prevCount => prevCount + 1); // prevCount = 0 ‚Üí 1
    setCount(prevCount => prevCount + 1); // prevCount = 1 ‚Üí 2
    // –¢–µ–ø–µ—Ä—å count —Å—Ç–∞–Ω–µ—Ç 2
  };

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={incrementTwice}>Increment Twice (Wrong)</button>
      <button onClick={incrementTwiceCorrect}>Increment Twice (Correct)</button>
    </div>
  );
}

// useReducer –¥–ª—è —Å–ª–æ–∂–Ω–æ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è
function todoReducer(state, action) {
  switch (action.type) {
    case 'ADD_TODO':
      return {
        ...state,
        todos: [...state.todos, {
          id: Date.now(),
          text: action.text,
          completed: false
        }]
      };

    case 'TOGGLE_TODO':
      return {
        ...state,
        todos: state.todos.map(todo =>
          todo.id === action.id
            ? { ...todo, completed: !todo.completed }
            : todo
        )
      };

    case 'REMOVE_TODO':
      return {
        ...state,
        todos: state.todos.filter(todo => todo.id !== action.id)
      };

    case 'SET_FILTER':
      return {
        ...state,
        filter: action.filter
      };

    default:
      return state;
  }
}

function TodoApp() {
  const [state, dispatch] = useReducer(todoReducer, {
    todos: [],
    filter: 'all'
  });

  const filteredTodos = state.todos.filter(todo => {
    if (state.filter === 'completed') return todo.completed;
    if (state.filter === 'active') return !todo.completed;
    return true; // 'all'
  });

  return (
    <div>
      <h2>Todos ({filteredTodos.length})</h2>
      <button onClick={() => dispatch({ type: 'SET_FILTER', filter: 'all' })}>
        All
      </button>
      <button onClick={() => dispatch({ type: 'SET_FILTER', filter: 'active' })}>
        Active
      </button>
      <button onClick={() => dispatch({ type: 'SET_FILTER', filter: 'completed' })}>
        Completed
      </button>

      <ul>
        {filteredTodos.map(todo => (
          <li key={todo.id}>
            <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
              {todo.text}
            </span>
            <button onClick={() => dispatch({ type: 'TOGGLE_TODO', id: todo.id })}>
              Toggle
            </button>
            <button onClick={() => dispatch({ type: 'REMOVE_TODO', id: todo.id })}>
              Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

// –ü–æ–¥—ä—ë–º —Å–æ—Å—Ç–æ—è–Ω–∏—è
function TemperatureInput({ scale, temperature, onTemperatureChange }) {
  return (
    <fieldset>
      <legend>Enter temperature in {scale}:</legend>
      <input
        value={temperature}
        onChange={(e) => onTemperatureChange(e.target.value)}
      />
    </fieldset>
  );
}

function TemperatureCalculator() {
  const [celsius, setCelsius] = useState('');
  const [fahrenheit, setFahrenheit] = useState('');

  const handleCelsiusChange = (value) => {
    setCelsius(value);
    if (value === '') {
      setFahrenheit('');
    } else {
      setFahrenheit((parseFloat(value) * 9/5 + 32).toFixed(2));
    }
  };

  const handleFahrenheitChange = (value) => {
    setFahrenheit(value);
    if (value === '') {
      setCelsius('');
    } else {
      setCelsius(((parseFloat(value) - 32) * 5/9).toFixed(2));
    }
  };

  return (
    <div>
      <TemperatureInput
        scale="celsius"
        temperature={celsius}
        onTemperatureChange={handleCelsiusChange}
      />
      <TemperatureInput
        scale="fahrenheit"
        temperature={fahrenheit}
        onTemperatureChange={handleFahrenheitChange}
      />
    </div>
  );
}

// –ë–∞–∑–æ–≤–æ–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ useEffect
function UserProfile({ userId }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  // –≠—Ñ—Ñ–µ–∫—Ç –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  useEffect(() => {
    // –≠—Ç–∞ —Ñ—É–Ω–∫—Ü–∏—è –≤—ã–ø–æ–ª–Ω–∏—Ç—Å—è –ø–æ—Å–ª–µ –∫–∞–∂–¥–æ–≥–æ —Ä–µ–Ω–¥–µ—Ä–∞
    console.log('Effect running for userId:', userId);

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö
    const fetchUserData = async () => {
      if (!userId) return;

      setLoading(true);
      try {
        const response = await fetch(`https://api.example.com/users/${userId}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Failed to fetch user:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();

    // –§—É–Ω–∫—Ü–∏—è –æ—á–∏—Å—Ç–∫–∏ (cleanup)
    return () => {
      console.log('Cleanup for userId:', userId);
      // –û—Ç–º–µ–Ω–∞ –∑–∞–ø—Ä–æ—Å–æ–≤, —É–¥–∞–ª–µ–Ω–∏–µ —Å–ª—É—à–∞—Ç–µ–ª–µ–π –∏ —Ç.–¥.
    };
  }, [userId]); // –ú–∞—Å—Å–∏–≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–µ–π

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      {user ? (
        <div>
          <h2>{user.name}</h2>
          <p>Email: {user.email}</p>
        </div>
      ) : (
        <p>No user selected</p>
      )}
    </div>
  );
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö —ç—Ñ—Ñ–µ–∫—Ç–æ–≤
function DocumentTitleUpdater({ pageTitle }) {
  // –≠—Ñ—Ñ–µ–∫—Ç 1: –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∑–∞–≥–æ–ª–æ–≤–∫–∞ –¥–æ–∫—É–º–µ–Ω—Ç–∞
  useEffect(() => {
    const originalTitle = document.title;
    document.title = pageTitle ? `${pageTitle} - My App` : 'My App';

    return () => {
      document.title = originalTitle; // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—ã–π –∑–∞–≥–æ–ª–æ–≤–æ–∫
    };
  }, [pageTitle]); // –ó–∞–≤–∏—Å–∏—Ç –æ—Ç pageTitle

  // –≠—Ñ—Ñ–µ–∫—Ç 2: –ü–æ–¥–ø–∏—Å–∫–∞ –Ω–∞ –∏–∑–º–µ–Ω–µ–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
  useEffect(() => {
    const handleResize = () => {
      console.log('Window size:', window.innerWidth, 'x', window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize); // –û—Ç–ø–∏—Å–∫–∞
    };
  }, []); // –¢–æ–ª—å–∫–æ –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏/—Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏

  // –≠—Ñ—Ñ–µ–∫—Ç 3: –¢–∞–π–º–µ—Ä
  useEffect(() => {
    const intervalId = setInterval(() => {
      console.log('Timer tick');
    }, 1000);

    return () => {
      clearInterval(intervalId); // –û—á–∏—Å—Ç–∫–∞ —Ç–∞–π–º–µ—Ä–∞
    };
  }, []);

  return <h1>{pageTitle}</h1>;
}

// useLayoutEffect –¥–ª—è —Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã—Ö –æ–ø–µ—Ä–∞—Ü–∏–π —Å DOM
function Tooltip({ children, targetRef }) {
  const [position, setPosition] = useState({ top: 0, left: 0 });

  useLayoutEffect(() => {
    if (targetRef.current) {
      const rect = targetRef.current.getBoundingClientRect();

      // –ò–∑–º–µ—Ä—è–µ–º –ø–æ–ª–æ–∂–µ–Ω–∏–µ –∏ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –°–ò–ù–•–†–û–ù–ù–û
      // —á—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –º–µ—Ä—Ü–∞–Ω–∏—è –ø—Ä–∏ —Ä–µ–Ω–¥–µ—Ä–µ
      setPosition({
        top: rect.bottom + window.scrollY + 5,
        left: rect.left + window.scrollX
      });
    }
  }, [targetRef]);

  return (
    <div 
      className="tooltip"
      style={{
        position: 'absolute',
        top: `${position.top}px`,
        left: `${position.left}px`
      }}
    >
      {children}
    </div>
  );
}

// –ö–ª–∞—Å—Å–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å –º–µ—Ç–æ–¥–∞–º–∏ –∂–∏–∑–Ω–µ–Ω–Ω–æ–≥–æ —Ü–∏–∫–ª–∞
class UserProfileClass extends React.Component {
  constructor(props) {
    super(props);
    this.state = { user: null, loading: false };
    console.log('constructor: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å–æ–∑–¥–∞–Ω');
  }

  componentDidMount() {
    console.log('componentDidMount: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–æ–±–∞–≤–ª–µ–Ω –≤ DOM');
    this.fetchUserData();

    // –ü–æ–¥–ø–∏—Å–∫–∞ –Ω–∞ —Å–æ–±—ã—Ç–∏—è
    window.addEventListener('resize', this.handleResize);
  }

  componentDidUpdate(prevProps) {
    console.log('componentDidUpdate: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –æ–±–Ω–æ–≤–∏–ª—Å—è');

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è –ø—Ä–æ–ø—Å–æ–≤
    if (this.props.userId !== prevProps.userId) {
      console.log('userId –∏–∑–º–µ–Ω–∏–ª—Å—è, –∑–∞–≥—Ä—É–∂–∞–µ–º –Ω–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ');
      this.fetchUserData();
    }
  }

  componentWillUnmount() {
    console.log('componentWillUnmount: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –±—É–¥–µ—Ç —É–¥–∞–ª—ë–Ω');

    // –û—á–∏—Å—Ç–∫–∞
    window.removeEventListener('resize', this.handleResize);
    // –û—Ç–º–µ–Ω–∞ –∑–∞–ø—Ä–æ—Å–æ–≤, –æ—á–∏—Å—Ç–∫–∞ —Ç–∞–π–º–µ—Ä–æ–≤
  }

  fetchUserData = async () => {
    const { userId } = this.props;
    if (!userId) return;

    this.setState({ loading: true });

    try {
      const response = await fetch(`https://api.example.com/users/${userId}`);
      const data = await response.json();
      this.setState({ user: data, loading: false });
    } catch (error) {
      console.error('Failed to fetch user:', error);
      this.setState({ loading: false });
    }
  };

  handleResize = () => {
    console.log('Window resized');
  };

  render() {
    console.log('render: –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Ä–µ–Ω–¥–µ—Ä–∏—Ç—Å—è');
