// –°–æ–±—ã—Ç–∏—è –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã —Å –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä–∞–º–∏
function KeyboardEvents() {
  const [keyLog, setKeyLog] = useState([]);

  const handleKeyDown = (event) => {
    const keyInfo = {
      key: event.key,
      code: event.code,
      ctrl: event.ctrlKey,
      alt: event.altKey,
      shift: event.shiftKey,
      meta: event.metaKey
    };

    setKeyLog(prev => [keyInfo, ...prev.slice(0, 5)]);

    // –ì–æ—Ä—è—á–∏–µ –∫–ª–∞–≤–∏—à–∏
    if (event.ctrlKey && event.key === 's') {
      event.preventDefault();
      console.log('Ctrl+S: Save document');
    }

    if (event.altKey && event.key === 'F4') {
      console.log('Alt+F4 pressed');
    }
  };

  return (
    <div>
      <input 
        type="text"
        onKeyDown={handleKeyDown}
        placeholder="Type here..."
      />
      <div>
        <h4>Recent keys:</h4>
        {keyLog.map((key, i) => (
          <div key={i}>
            {key.key} {key.ctrl && 'Ctrl'} {key.alt && 'Alt'} {key.shift && 'Shift'}
          </div>
        ))}
      </div>
    </div>
  );
}

