function EventDelegation() {
  const listRef = useRef(null);

  useEffect(() => {
    const handleListClick = (event) => {
      // –û–ø—Ä–µ–¥–µ–ª—è–µ–º, –±—ã–ª –ª–∏ –∫–ª–∏–∫ –Ω–∞ —ç–ª–µ–º–µ–Ω—Ç–µ —Å–ø–∏—Å–∫–∞
      if (event.target.tagName === 'LI') {
        const itemId = event.target.dataset.id;
        console.log('Clicked item:', itemId);

        // –ò–∑–º–µ–Ω—è–µ–º —Å—Ç–∏–ª—å –∫–ª–∏–∫–Ω—É—Ç–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
        event.target.style.backgroundColor = '#e0e0e0';

        setTimeout(() => {
          event.target.style.backgroundColor = '';
        }, 300);
      }
    };

    const list = listRef.current;
    list.addEventListener('click', handleListClick);

    return () => {
      list.removeEventListener('click', handleListClick);
    };
  }, []);

  const items = [
    { id: 1, text: 'Item 1' },
    { id: 2, text: 'Item 2' },
    { id: 3, text: 'Item 3' },
    { id: 4, text: 'Item 4' },
    { id: 5, text: 'Item 5' }
  ];

  return (
    <div>
      <h3>Event Delegation Example</h3>
      <p>Only one event listener for all items</p>

      <ul ref={listRef} style={{ listStyle: 'none', padding: 0 }}>
        {items.map(item => (
          <li 
            key={item.id}
            data-id={item.id}
            style={{
              padding: '10px',
              margin: '5px 0',
              border: '1px solid #ddd',
              cursor: 'pointer'
            }}
          >
            {item.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

