function CustomEvents() {
  const dispatchCustomEvent = () => {
    // –°–æ–∑–¥–∞–Ω–∏–µ –∏ –¥–∏—Å–ø–µ—Ç—á–µ—Ä–∏–∑–∞—Ü–∏—è –∫–∞—Å—Ç–æ–º–Ω–æ–≥–æ —Å–æ–±—ã—Ç–∏—è
    const event = new CustomEvent('reactapp:notification', {
      detail: {
        message: 'Hello from custom event!',
        timestamp: new Date().toISOString()
      },
      bubbles: true,
      cancelable: true
    });

    window.dispatchEvent(event);
  };

  useEffect(() => {
    const handleCustomEvent = (event) => {
      console.log('Custom event received:', event.detail);
      alert(`Custom: ${event.detail.message}`);
    };

    window.addEventListener('reactapp:notification', handleCustomEvent);

    return () => {
      window.removeEventListener('reactapp:notification', handleCustomEvent);
    };
  }, []);

  return (
    <button onClick={dispatchCustomEvent}>
      Dispatch Custom Event
    </button>
  );
}

