// –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∞—è —Ñ—É–Ω–∫—Ü–∏—è —Å—Ä–∞–≤–Ω–µ–Ω–∏—è –¥–ª—è React.memo
const ComplexUserProfile = React.memo(function ComplexUserProfile({ 
  user, 
  settings, 
  onAction 
}) {
  console.log('ComplexUserProfile rendered');

  // –î–æ—Ä–æ–≥–æ—Å—Ç–æ—è—â–∏–µ –≤—ã—á–∏—Å–ª–µ–Ω–∏—è
  const userStats = calculateUserStats(user);

  return (
    <div className="complex-user-profile">
      <h3>{user.name}</h3>
      <p>Score: {userStats.score}</p>
      <p>Level: {userStats.level}</p>
      <button onClick={() => onAction('message', user.id)}>
        Send Message
      </button>
    </div>
  );
}, arePropsEqual); // –í—Ç–æ—Ä–æ–π –∞—Ä–≥—É–º–µ–Ω—Ç ‚Äî —Ñ—É–Ω–∫—Ü–∏—è —Å—Ä–∞–≤–Ω–µ–Ω–∏—è

// –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∞—è —Ñ—É–Ω–∫—Ü–∏—è —Å—Ä–∞–≤–Ω–µ–Ω–∏—è –ø—Ä–æ–ø—Å–æ–≤
function arePropsEqual(prevProps, nextProps) {
  /*
  –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç true, –µ—Å–ª–∏ —Ä–µ–Ω–¥–µ—Ä–∏—Ç—å –Ω–µ –Ω—É–∂–Ω–æ (–ø—Ä–æ–ø—Å—ã —Ä–∞–≤–Ω—ã)
  –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç false, –µ—Å–ª–∏ —Ä–µ–Ω–¥–µ—Ä–∏—Ç—å –Ω—É–∂–Ω–æ (–ø—Ä–æ–ø—Å—ã –∏–∑–º–µ–Ω–∏–ª–∏—Å—å)
  */

  // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º —Ç–æ–ª—å–∫–æ –≤–∞–∂–Ω—ã–µ –ø–æ–ª—è –æ–±—ä–µ–∫—Ç–∞ user
  const userChanged = 
    prevProps.user.id !== nextProps.user.id ||
    prevProps.user.name !== nextProps.user.name ||
    prevProps.user.email !== nextProps.user.email;

  // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º –Ω–µ–∫–æ—Ç–æ—Ä—ã–µ –ø–æ–ª—è settings, –∫–æ—Ç–æ—Ä—ã–µ –Ω–µ –≤–ª–∏—è—é—Ç –Ω–∞ —Ä–µ–Ω–¥–µ—Ä
  const settingsChanged = 
    prevProps.settings.theme !== nextProps.settings.theme ||
    prevProps.settings.language !== nextProps.settings.language;
  // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º settings.notifications, —Ç–∞–∫ –∫–∞–∫ –æ–Ω–∏ –Ω–µ –≤–ª–∏—è—é—Ç –Ω–∞ UI

  // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é onAction –ø–æ —Å—Å—ã–ª–∫–µ
  const onActionChanged = prevProps.onAction !== nextProps.onAction;

  // –ï—Å–ª–∏ –Ω–∏—á–µ–≥–æ –≤–∞–∂–Ω–æ–µ –Ω–µ –∏–∑–º–µ–Ω–∏–ª–æ—Å—å ‚Äî –Ω–µ —Ä–µ–Ω–¥–µ—Ä–∏—Ç—å
  return !(userChanged || settingsChanged || onActionChanged);
}

// –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è (–∏–º–∏—Ç–∞—Ü–∏—è –¥–æ—Ä–æ–≥–∏—Ö –≤—ã—á–∏—Å–ª–µ–Ω–∏–π)
function calculateUserStats(user) {
  // –ò–º–∏—Ç–∞—Ü–∏—è —Å–ª–æ–∂–Ω—ã—Ö –≤—ã—á–∏—Å–ª–µ–Ω–∏–π
  let score = 0;
  for (let i = 0; i < 100000; i++) {
    score += Math.sqrt(i) * user.id;
  }

  return {
    score: Math.round(score),
    level: Math.floor(user.id / 10) + 1
  };
}

