function ComplexComponent() {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState('');
  const [sortBy, setSortBy] = useState('name');

  // 1. –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–±—ã—Ç–∏–π —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏
  const handleItemClick = useCallback((itemId, event) => {
    console.log('Item clicked:', itemId);
    console.log('Event type:', event.type);
  }, []); // –ù–µ –∑–∞–≤–∏—Å–∏—Ç –æ—Ç —Å–æ—Å—Ç–æ—è–Ω–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞

  // 2. –§—É–Ω–∫—Ü–∏—è —Å debounce (–∑–∞–¥–µ—Ä–∂–∫–æ–π)
  const handleSearch = useCallback(
    debounce((searchTerm) => {
      console.log('Searching for:', searchTerm);
      // API –∑–∞–ø—Ä–æ—Å —Å searchTerm
    }, 300),
    [] // debounce —Ñ—É–Ω–∫—Ü–∏—è —Å—Ç–∞–±–∏–ª—å–Ω–∞
  );

  // 3. –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏ –¥–∞–Ω–Ω—ã—Ö
  const sortData = useCallback((items) => {
    const sorted = [...items];

    if (sortBy === 'name') {
      sorted.sort((a, b) => a.name.localeCompare(b.name));
    } else if (sortBy === 'date') {
      sorted.sort((a, b) => new Date(b.date) - new Date(a.date));
    } else if (sortBy === 'price') {
      sorted.sort((a, b) => a.price - b.price);
    }

    return sorted;
  }, [sortBy]); // –ó–∞–≤–∏—Å–∏—Ç –æ—Ç sortBy

  // 4. –§—É–Ω–∫—Ü–∏—è –¥–ª—è –º–µ–º–æ–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –≤—ã—á–∏—Å–ª–µ–Ω–∏—è
  const filteredAndSortedData = useMemo(() => {
    const filtered = data.filter(item => 
      item.name.toLowerCase().includes(filter.toLowerCase())
    );
    return sortData(filtered);
  }, [data, filter, sortData]); // sortData –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç—è—Ö

  // 5. –§—É–Ω–∫—Ü–∏—è, –∏—Å–ø–æ–ª—å–∑—É–µ–º–∞—è –≤ useEffect
  const fetchData = useCallback(async () => {
    try {
      const response = await fetch(`/api/data?filter=${filter}`);
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  }, [filter]); // –ó–∞–≤–∏—Å–∏—Ç –æ—Ç filter

  useEffect(() => {
    fetchData();
  }, [fetchData]); // fetchData –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç—è—Ö useEffect

  return (
    <div>
      <input
        value={filter}
