// –ë–∞–∑–æ–≤—ã–π –ø—Ä–∏–º–µ—Ä HOC –¥–ª—è –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏—è
function withLogger(WrappedComponent) {
  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –Ω–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç
  return function WithLoggerComponent(props) {
    // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
    useEffect(() => {
      console.log(`Component ${WrappedComponent.displayName || WrappedComponent.name} mounted`);

      return () => {
        console.log(`Component ${WrappedComponent.displayName || WrappedComponent.name} unmounted`);
      };
    }, []);

    // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–æ–ø—Å–æ–≤
    console.log(`Rendering ${WrappedComponent.displayName || WrappedComponent.name} with props:`, props);

    // –†–µ–Ω–¥–µ—Ä–∏–º –æ–±—ë—Ä–Ω—É—Ç—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å–æ –≤—Å–µ–º–∏ –ø—Ä–æ–ø—Å–∞–º–∏
    return <WrappedComponent {...props} />;
  };
}

// HOC –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –∑–∞–≥—Ä—É–∑–∫–∏
function withLoading(WrappedComponent) {
  return function WithLoadingComponent({ isLoading, ...restProps }) {
    if (isLoading) {
      return (
        <div className="loading-container">
          <div className="spinner"></div>
          <p>Loading...</p>
        </div>
      );
    }

    return <WrappedComponent {...restProps} />;
  };
}

// HOC –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –æ—à–∏–±–æ–∫
function withErrorBoundary(WrappedComponent) {
  return class WithErrorBoundaryComponent extends React.Component {
    constructor(props) {
      super(props);
      this.state = { hasError: false, error: null };
    }

    static getDerivedStateFromError(error) {
      return { hasError: true, error };
    }

    componentDidCatch(error, errorInfo) {
      console.error('Error caught by HOC:', error, errorInfo);
    }

    render() {
      if (this.state.hasError) {
        return (
          <div className="error-boundary">
            <h3>Something went wrong</h3>
            <p>{this.state.error?.message}</p>
            <button onClick={() => this.setState({ hasError: false })}>
              Try again
            </button>
          </div>
        );
      }

      return <WrappedComponent {...this.props} />;
    }
  };
}

