// HOC –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏–∏
function withAuth(WrappedComponent, requiredRole = null) {
  return function WithAuthComponent(props) {
    // –í —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—è –º–æ–≥–ª–∞ –±—ã –±—ã—Ç—å –∏–∑ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
    const { isAuthenticated, user, login } = useAuth();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—é
    if (!isAuthenticated) {
      return (
        <div className="auth-required">
          <p>You need to be logged in to view this page.</p>
          <button onClick={login}>Login</button>
        </div>
      );
    }

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ä–æ–ª—å, –µ—Å–ª–∏ —Ç—Ä–µ–±—É–µ—Ç—Å—è
    if (requiredRole && user.role !== requiredRole) {
      return (
        <div className="role-required">
          <h3>Access Denied</h3>
          <p>You don't have permission to view this page.</p>
          <p>Required role: {requiredRole}</p>
          <p>Your role: {user.role}</p>
        </div>
      );
    }

    // –ï—Å–ª–∏ –≤—Å—ë OK, —Ä–µ–Ω–¥–µ—Ä–∏–º –æ–±—ë—Ä–Ω—É—Ç—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–º –ø—Ä–æ–ø—Å–æ–º user
    return <WrappedComponent {...props} user={user} />;
  };
}

// HOC –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö
function withDataFetching(urlPropName, dataPropName = 'data') {
  return function(WrappedComponent) {
    return function WithDataFetchingComponent(props) {
      const [data, setData] = useState(null);
      const [loading, setLoading] = useState(true);
      const [error, setError] = useState(null);

      const url = props[urlPropName]; // –ü–æ–ª—É—á–∞–µ–º URL –∏–∑ –ø—Ä–æ–ø—Å–æ–≤

      useEffect(() => {
        if (!url) return;

        const fetchData = async () => {
          try {
            setLoading(true);
            const response = await fetch(url);

            if (!response.ok) {
              throw new Error(`HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            setData(result);
            setError(null);
          } catch (err) {
            setError(err.message);
            console.error('Fetch error:', err);
          } finally {
            setLoading(false);
          }
        };

        fetchData();
      }, [url]);

      // –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –ø—Ä–æ–ø—Å—ã –¥–ª—è –æ–±—ë—Ä–Ω—É—Ç–æ–≥–æ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
      const injectedProps = {
        [dataPropName]: data,
        loading,
        error,
        refetch: () => {
          setLoading(true);
          // –ü–æ–≤—Ç–æ—Ä–Ω–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö
        }
      };

      // –ï—Å–ª–∏ –∑–∞–≥—Ä—É–∑–∫–∞, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä
      if (loading) {
        return <div className="loading-spinner">Loading...</div>;
      }

      // –ï—Å–ª–∏ –æ—à–∏–±–∫–∞, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ
      if (error) {
        return (
          <div className="error-message">
            <h3>Error loading data</h3>
            <p>{error}</p>
            <button onClick={() => setLoading(true)}>Retry</button>
          </div>
        );
      }

      // –†–µ–Ω–¥–µ—Ä–∏–º –æ–±—ë—Ä–Ω—É—Ç—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–º–∏ –ø—Ä–æ–ø—Å–∞–º–∏
      return <WrappedComponent {...props} {...injectedProps} />;
    };
  };
}

