// –ü—Ä–æ–¥–≤–∏–Ω—É—Ç—ã–π HOC —Å –ø–æ–¥–¥–µ—Ä–∂–∫–æ–π —Å—Ç–∞—Ç–∏—á–µ—Å–∫–∏—Ö –º–µ—Ç–æ–¥–æ–≤
import hoistNonReactStatics from 'hoist-non-react-statics';

function withAnalytics(WrappedComponent, eventName) {
  class WithAnalyticsComponent extends React.Component {
    // –°—Ç–∞—Ç–∏—á–µ—Å–∫–æ–µ —Å–≤–æ–π—Å—Ç–≤–æ
    static displayName = `WithAnalytics(${WrappedComponent.displayName || WrappedComponent.name})`;

    componentDidMount() {
      // –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–±—ã—Ç–∏—è –∞–Ω–∞–ª–∏—Ç–∏–∫–∏
      this.trackEvent('component_mounted');
    }

    componentDidUpdate(prevProps) {
      // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –∏–∑–º–µ–Ω–µ–Ω–∏–π –ø—Ä–æ–ø—Å–æ–≤
      if (this.props.userId !== prevProps.userId) {
        this.trackEvent('user_changed');
      }
    }

    trackEvent = (action) => {
      // –ò–º–∏—Ç–∞—Ü–∏—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –≤ –∞–Ω–∞–ª–∏—Ç–∏–∫—É
      console.log(`Analytics: ${eventName}_${action}`, {
        component: WrappedComponent.displayName || WrappedComponent.name,
        props: this.props,
        timestamp: new Date().toISOString()
      });
    };

    handleClick = (e) => {
      // –ü–µ—Ä–µ—Ö–≤–∞—Ç –∫–ª–∏–∫–æ–≤
      this.trackEvent('click');

      // –í—ã–∑–æ–≤ –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω–æ–≥–æ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∞, –µ—Å–ª–∏ –µ—Å—Ç—å
      if (this.props.onClick) {
        this.props.onClick(e);
      }
    };

    render() {
      // –§–∏–ª—å—Ç—Ä—É–µ–º —Å–ø–µ—Ü–∏—Ñ–∏—á–Ω—ã–µ –ø—Ä–æ–ø—Å—ã HOC
      const { analyticsId, ...restProps } = this.props;

      return (
        <WrappedComponent
          {...restProps}
          onClick={this.handleClick}
          analyticsId={analyticsId || 'default'}
        />
      );
    }
  }

  // –ö–æ–ø–∏—Ä—É–µ–º —Å—Ç–∞—Ç–∏—á–µ—Å–∫–∏–µ –º–µ—Ç–æ–¥—ã —Å –æ–±–æ—Ä–∞—á–∏–≤–∞–µ–º–æ–≥–æ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
  hoistNonReactStatics(WithAnalyticsComponent, WrappedComponent);

  return WithAnalyticsComponent;
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç —Å–æ —Å—Ç–∞—Ç–∏—á–µ—Å–∫–∏–º–∏ –º–µ—Ç–æ–¥–∞–º–∏
class ProductCard extends React.Component {
  static defaultProps = {
    variant: 'default'
  };

  static getProductCategory(productId) {
    // –°—Ç–∞—Ç–∏—á–µ—Å–∫–∏–π –º–µ—Ç–æ–¥
    return productId > 100 ? 'premium' : 'standard';
  }

  render() {
    const { product, onClick } = this.props;

    return (
      <div className="product-card" onClick={onClick}>
        <h3>{product.name}</h3>
        <p>${product.price}</p>
      </div>
    );
  }
}

// –ü—Ä–∏–º–µ–Ω–µ–Ω–∏–µ HOC (—Å—Ç–∞—Ç–∏—á–µ—Å–∫–∏–µ –º–µ—Ç–æ–¥—ã —Å–æ—Ö—Ä–∞–Ω—è—é—Ç—Å—è)
const ProductCardWithAnalytics = withAnalytics(ProductCard, 'product_card');

// –°—Ç–∞—Ç–∏—á–µ—Å–∫–∏–µ –º–µ—Ç–æ–¥—ã –¥–æ—Å—Ç—É–ø–Ω—ã
console.log(ProductCardWithAnalytics.defaultProps); // { variant: 'default' }
console.log(ProductCardWithAnalytics.getProductCategory(150)); // 'premium'

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
function ProductListing() {
  const product = { id: 123, name: 'Laptop', price: 999.99 };

  return (
    <ProductCardWithAnalytics
      product={product}
      analyticsId="homepage"
      onClick={() => console.log('Card clicked')}
    />
  );
}
